def CompUn(Lst):    '''Fonction qui va renvoyer le complement a 1 du nombre binaire    passe en entree sous forme de liste'''    # On initialise le resultat: une liste vide    resultat = []    # On boucle sur la totalite de la liste passee en argument    for i in range(len(Lst)):        # Pour chaque element de la liste, on ajoute son contraire dans la        # liste resultat        if Lst[i] == 0:            resultat.append(1)        else:            resultat.append(0)    # Et pour finir on renvoie la liste resultat ainsi completee    return resultat