def decoupage(chn):    # Récupération de la longueur de la chaine    LongChn = len(chn)    # Initialisation du résultat - chaîne vide    resultat = ""    # Parcours de tous les éléments (caractères) de la chaine    for i in range(LongChn):        # On n'ajoute de virgue que s'il y a déjà des caractères dans le résultat        if resultat != "":            resultat += ","        # On ajoute le caractère en cours        resultat += chn[i]        return resultat