def TrouveMin(liste):    # On initialise le minimum à la première valeur d'élément de la liste    Min = liste[0]    # On compte le nombre d'éléments de la liste    NbElts = len(liste)        # On parcourt la liste    for i in range(NbElts):        if liste[i] < Min:            # On remplace par une nouvelle valeur de max            Min = liste[i]    # On a terminé, on retourne le max trouvé    return Min