def EstPresent(liste, element):    # On initialise le résultat à Faux - on le changera si on trouve l'élément dans la liste    resultat = False    # On compte le nombre d'éléments de la liste    NbElts = len(liste)        # On parcourt la liste    for i in range(NbElts):        if liste[i] == element:            # On a trouvé l'élément, le résultat va donc être vrai            resultat = True    # On a terminé, on retourne le max trouvé    return resultat