def EstPresentOptim(liste, element):    # On n'utilise pas de variable resultat cette fois - on va renvoyer directement la valeur depuis la boucle    NbElts = len(liste)        for i in range(NbElts):        if liste[i] == element:            # On a trouvé l'élément: on fait un "return" True directement. La commande "return" stoppe la fonction et donc la boucle - on ne va donc pas parcourir inutilement le reste de la liste.            return True    # Cette fois, si on est arrivé jusqu'ici, c'est qu'on n'a pas trouvé l'élément. On renvoie donc False    return False