def Longueurs(listeMots):    # On initialise la liste de longueurs avec une liste vide    listeLongueurs = []        # Comme d'habitude, on compte les éléments de la liste    NbElts = len(listeMots)    # Puis on boucle sur tous les éléments    for i in range(NbElts):        # On ajoute la longueur du mot en cours avec la méthode append        LongueurDuMot = len(listeMots[i])        listeLongueurs.append(LongueurDuMot)    # On renvoie la liste ainsi constituée    return listeLongueurs