def Mediane(n1, n2, n3):    '''    Fonction qui prend en entrée trois valeurs numériques (int ou float).    Elle renvoie la médiane de ces trois valeurs quand celle-ci existe.    Si elle n'existe pas (deux valeurs égales, la troisième différente),    elle renvoie None.    Par ex: Mediane(0, 2, 1) renverra 1;    Mediane(1, 1, 1) renverra 1;    Mediane(0, 2, 2) renverra None.    '''    if n1 < n2 < n3 or n3 < n2 < n1:        return n2    elif n1 < n3 < n2 or n2 < n3 < n1:        return n3    elif n2 < n1 < n3 or n3 < n1 < n2:        return n1    elif n1 == n2 and n2 == n3:        return n1    else:        return None