def EstPalindrome(chn):    '''    Fonction qui prend en entrée une chaine de caractères.    Elle renvoie Vrai si la chaine est un palindrome, Faux sinon.    Par ex: EstPalindrome("abba") renverra Vrai;    EstPalindrome("abbac") renverra Faux.    '''    Long = len(chn)    if Long % 2 == 0:        Milieu = Long // 2        # Utilisation de la division entière ("//" et non "/" pour la division simple) de manière à avoir des entiers et non des float    else:        Milieu = (Long - 1) // 2    for i in range(Milieu):        if chn[i] != chn[Long - 1 - i]: # Sans le -1 on aura une erreur            return False    return True