def TriBulles(liste):    '''    Fonction qui effectue le tri par permutations de la liste passée en entrée.    '''    n = len(liste)    for i in range(n):        for j in range(n-1):            if liste[j] > liste[j+1]:                # Cette syntaxe permet d'affecter deux variables simultanément et donc de ne pas passer par une variable intermédiaire                liste[j], liste[j+1] = liste[j+1], liste[j]    return liste