# On crée ici plusieurs tableaux en utilisant la syntaxe par compréhension# (on la reverra plus en détails plus tard)# On va utiliser le module random pour générer des nombres au hasardimport random as r# T1: 20.000 nombres entiers compris entre 0 et 100.000, dans un ordre complètement aléatoireT1 = [r.randint(0,1000000) for i in range(20000)]# T2: les nombres 0 à 19.999, dans l'ordre croissantT2 = [i for i in range(20000)]# T3: tableau en deux moitiés:#       - Indices 0 à 9.999 (Ta): les nombres de 0 à 9.999, dans l'ordre#       - Indices 10.000 à 19.999 (Tb): 10.000 nombres au hasard, en ordre aléatoireTa = [i for i in range(10000)]Tb = [r.randint(0,1000000) for i in range(10000)]T3 = Ta + Tb# T3bis: les mêmes deux moitiés que T3, mais dans l'ordre inverse (Tb puis Ta)T3bis = Tb + Ta# T4: le même tableau que T2, mais en ordre exactement inverse (19.999 à 0, dans l'ordre)T4 = [19999 - i for i in range(20000)]