def TriBullesOptim(liste):    '''    Fonction qui effectue le tri par permutations de la liste passée en entrée.    '''    n = len(liste)    for i in range(n):        # On ne regarde dans la boucle interne que les éléments qui ne sont pas déjà triés        for j in range(n-i-1):            if liste[j] > liste[j+1]:                liste[j], liste[j+1] = liste[j+1], liste[j]    return liste