def DevineNb():    '''    Fonction qui s'appuie sur la recherche dichotomique pour deviner un nombre entre 0 et 100 auquel pense l'utilisateur.    '''    debut = 0    fin = 100    # Compteur de coups avant d'avoir deviné    cps = 0    print("Vous devez penser à nombre compris entre 0 et 100 et je dois le deviner. C'est parti...")    while fin >= debut:        cps += 1        milieu = (fin + debut) // 2        print("Je devine que le nombre est", milieu, ".")        print("Si j'ai raison, entrez 1; si votre nombre est supérieur à", milieu, "entrez 2; s'il est inférieur, entrez 3.")        rep = int(input("Votre réponse?  "))        if rep == 1:            print("J'ai trouvé en", cps, "coup(s)!")            # Un return force à sortir de la fonction            return        # Application stricte de l'algorithme de recherche dichotomique        elif rep == 2:            debut = milieu + 1        else:            fin = milieu - 1