def compare_tableaux(t, u):        # Cas le plus simple pour commencer    if len(t) != len(u):        return False        # On appelle la fonction occ sur les deux tableaux    dt = Occ(t)    du = Occ(u)    # On compare ensuite les deux dictionnaires produits    for val in dt:        if val not in du:            # Une valeur présente dans t qui n'est pas dans u            return False        elif dt[val] != du[val]:            # Une valeur présente dans les deux mais pas le même nombre de fois            return False    # Démarche habituelle: on n'est tombé dans aucun des cas False - on retourne donc True    return True