# Import des éléments de la bibliothèqueimport pygamefrom pygame.locals import *# Initialisationpygame.init()# Ouverture de la fenêtrefenetre = pygame.display.set_mode((640, 480))fenetre.fill([10,186,181])# Mise à jour de l'affichagepygame.display.flip()# Boucle des événementscontinuer = Truewhile continuer:    for evenement in pygame.event.get():    #Attente des événements        if evenement.type == QUIT:            continuer = False# Sortiepygame.quit()